source.buildSpec:
{
  "version": "0.2",
  "phases": {
    "build": {
      "commands": [
        "npm install -g aws-cdk",
        "curl -sSL https://install.python-poetry.org | python3 -",
        "poetry install",
        "cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk.out",
    "files": "**/*"
  }
}
